export class WeatherService {
  constructor(http) {
    this.http = http;
  }

  getUserData() {
    return this.http.get(`${environment.apiUrl}.ip.json?key=${environment.apiKey}&q=auto:ip`);
  }

  getYesterdaysWeather(userData, date) {
    return this.http.get(`${environment.apiUrl}history.json?key=${environment.apiKey}&q=${userData.lat},${userData.lon}&dt=${date}`);
  }

}
